    private void Remove()
    {
        Console.WriteLine(mainClass+"sasad");
        if (!mainClass.Equals("hidden"))
        {
            secondaryClass = "";
            mainClass = "hidden";
        }
        else
        {
            secondaryClass = "hidden";
            mainClass = "";
        }
    }

    private void Confirm()
    {
       
        AdultService.Remove(Adult.Id);
        mainClass = "";
        NavigationManager.NavigateTo("/fetchdata");
    }


